mapper.update:
		UPDATE
		ORGA.PER_ACC_RELA
		SET PASSWORD=#{password}
		WHERE ACC_ID=${accId}

mapper.select:
select t3.ljpym, t2.acc_id, T2.ACC_NAME from orga.ACCOUNT t2, orga.department t3 where t2.acc_id in (select t1.acc_id from orga.ACC_ROL_RELA t1 where t1.rol_id in (select t.rol_id from orga.ROLE t where t.rol_name = #{rol_name})) and t2.fenlei01 = t3.dep_id
and t3.ljpym = #{ljpym}
and t3.ljpym in(${ljpyms})
and t2.acc_name = #{acc_name}
